# Main TF file

# Provider Configuration
provider "azurerm" {
  version = "2.44.0"
  features {}
}

# Terraform Block
terraform {
  required_version = ">= 1.1.4"

  required_providers {
    azurerm = {
      source = "hashicorp/azurerm"
    }
  }
}

# Load Balancer Configuration
resource "azurerm_public_ip" "load_balancer_static_ip" {
  name                = "${var.resource_group_name}-static-ip"
  resource_group_name = var.resource_group_name
  location            = var.location
  allocation_method   = "Static"
  sku                 = "Standard"

  depends_on = [var.resource_group]
}

resource "azurerm_public_ip_prefix" "load_balancer_static_ip_prefix" {
  name               = "${var.resource_group_name}-static-ip-prefix"
  resource_group_name = var.resource_group_name
  location            = var.location
  prefix_length       = 28

  depends_on = [var.resource_group]
}

resource "azurerm_lb" "load_balancer" {
  name                = "${var.resource_group_name}-lb"
  resource_group_name = var.resource_group_name
  location            = var.location
  sku                 = "Standard"

  frontend_ip_configuration {
    name                 = "${var.resource_group_name}-frontend-ip"
    public_ip_address_id = azurerm_public_ip.load_balancer_static_ip.id
  }

  depends_on = [var.resource_group]
}

