Here is the main.tf file:
terraform {
  required_version = ">= 0.14"
  required_providers {
    azurerm = {
      source  = "hashicorp/azurerm"
      version = "~> 2.94"
    }
  }
}

provider "azurerm" {
  features {}
}

resource "azurerm_resource_group" "vm" {
  name     = var.resource_group_name
  location = var.location
}

resource "azurerm_virtual_network" "vm" {
  name                = "vm-vnet"
  resource_group_name = azurerm_resource_group.vm.name
  location            = azurerm_resource_group.vm.location
  address_space       = ["10.0.0.0/16"]
}

resource "azurerm_subnet" "vm" {
  name                 = "vm-subnet"
  resource_group_name = azurerm_resource_group.vm.name
  virtual_network_name = azurerm_virtual_network.vm.name
  address_prefix       = "10.0.1.0/24"
}

resource "azurerm_public_ip" "vm" {
  name                = "vm-public-ip"
  resource_group_name = azurerm_resource_group.vm.name
  location            = azurerm_resource_group.vm.location
  allocation_method   = "Dynamic"
}

resource "azurerm_network_interface" "vm" {
  name                = "vm-nic"
  resource_group_name = azurerm_resource_group.vm.name
  location            = azurerm_resource_group.vm.location

  ip_configuration {
    name                          = "internal-dns"
    public_ip_address_id          = azurerm_public_ip.vm.id
    subnet_id                     = azurerm_subnet.vm.id
    private_ip_address_version   = "IPv4"
  }
}

resource "azurerm_virtual_machine" "vm" {
  name                  = "vm-vm"
  resource_group_name   = azurerm_resource_group.vm.name
  location              = azurerm_resource_group.vm.location
  network_interface_ids = [azurerm_network_interface.vm.id]

  vm_size               = var.vm_size

  storage_image_reference {
    publisher = "MicrosoftWindowsServer"
    offer     = "WindowsServer"
    sku       = "2016-Datacenter"
    version   = "latest"
  }

  os_profile {
    computer_name  = "vm-computer-name"
    admin_username = "adminuser"
  }

  os_profile_windows_config {
    provision_vm_agent = true
  }
}

terraformFiles.variables.tf:
variable "resource_group_name" {
  type        = string
  description = "The name of the resource group"
  default     = "my-resource-group"
}

variable "location" {
  type        = string
  description = "The location for all resources"
  default     = "West US"
}

variable "vm_size" {
  type        = string
  description = "The size of the virtual machine"
  default     = "Standard_DS2_v2"

  validation {
    condition     = contains(["Standard_DS1_v2", "Standard_DS2_v2", "Standard_DS3_v2"], var.vm_size)
    error_message = "vm_size must be one of Standard_DS1_v2, Standard_DS2_v2 or Standard_DS3_v2."
  }
}

terraformFiles.outputs.tf:
