provider "azurerm" {
  features {}
}

resource azurerm_public_ip "lb" {
  name                = "${var.resource_group_name}-LB-PIP"
  location            = var.location
  resource_group_name = var.resource_group_name
  allocation_method   = "Static"
}

resource azurerm_public_ip "lb2" {
  name                = "${var.resource_group_name}-LB2-PIP"
  location            = var.location
  resource_group_name = var.resource_group_name
  allocation_method   = "Static"
}

resource azurerm_lb "lb" {
  name                = "${var.resource_group_name}-LB"
  location            = var.location
  resource_group_name = var.resource_group_name

  sku                           = "Standard"
  frontend_ip_configuration_names = [azurerm_public_ip.lb.name, azurerm_public_ip.lb2.name]
}

resource azurerm_lb_backend_address_pool "lb" {
  name                                      = "${var.resource_group_name}-LB-BEPOOL"
  resource_group_name                      = var.resource_group_name
  loadbalancer_id                          = azurerm_lb.lb.id
}

resource azurerm_lb_rule "http" {
  name                                       = "${var.resource_group_name}-HTTP-RULE"
  resource_group_name                       = var.resource_group_name
  loadbalancer_id                           = azurerm_lb.lb.id
  protocol                                  = "Tcp"
  frontend_port                             = 80
  backend_address_pool_ids                 = [azurerm_lb_backend_address_pool.lb.id]
  backend_http_settings_names               = ["${var.resource_group_name}-HTTP-SETTING"]
}

resource azurerm_lb_rule "https" {
  name                                       = "${var.resource_group_name}-HTTPS-RULE"
  resource_group_name                       = var.resource_group_name
  loadbalancer_id                           = azurerm_lb.lb.id
  protocol                                  = "Tcp"
  frontend_port                             = 443
  backend_address_pool_ids                 = [azurerm_lb_backend_address_pool.lb.id]
  backend_http_settings_names               = ["${var.resource_group_name}-HTTPS-SETTING"]
}

resource azurerm_lb_nat_rule "http" {
  name                                       = "${var.resource_group_name}-HTTP-NAT"
  resource_group_name                       = var.resource_group_name
  loadbalancer_id                           = azurerm_lb.lb.id
  protocol                                  = "Tcp"
  frontend_port                             = 80
  backend_address_pool_ids                 = [azurerm_lb_backend_address_pool.lb.id]
  frontend_ip_configuration_name            = azurerm_public_ip.lb.name
}

resource azurerm_lb_nat_rule "https" {
  name                                       = "${var.resource_group_name}-HTTPS-NAT"
  resource_group_name                       = var.resource_group_name
  loadbalancer_id                           = azurerm_lb.lb.id
  protocol                                  = "Tcp"
  frontend_port                             = 443
  backend_address_pool_ids                 = [azurerm_lb_backend_address_pool.lb.id]
  frontend_ip_configuration_name            = azurerm_public_ip.lb2.name
}

resource azurerm_lb_front_end_ip_config "http" {
  name                                      = "${var.resource_group_name}-HTTP-FEIP"
  resource_group_name                      = var.resource_group_name
  loadbalancer_id                          = azurerm_lb.lb.id
  public_ip_address                         = azurerm_public_ip.lb.id
}

resource azurerm_lb_front_end_ip_config "https" {
  name                                      = "${var.resource_group_name}-HTTPS-FEIP"
  resource_group_name                      = var.resource_group_name
  loadbalancer_id                          = azurerm_lb.lb.id
  public_ip_address                         = azurerm_public_ip.lb2.id
}

